(825, 414, '_resources', '<p>You can download the sample dataset to start practicing your solution.</p>\r\n\r\n<h3 class=\"fs-5 my-4 text-success\">Dataset</h3>\r\n\r\n<!-- Table start -->\r\n<div class=\"table-responsive\">\r\n  <table class=\"table table-striped\">\r\n    <thead>\r\n      <tr>\r\n        <th scope=\"col\">id</th>\r\n        <th scope=\"col\">email</th>\r\n        <th scope=\"col\">first_name</th>\r\n        <th scope=\"col\">last_name</th>\r\n      </tr>\r\n    </thead>\r\n    <tbody>\r\n      <tr>\r\n        <td>003500M7Q93CKG2je2</td>\r\n        <td>shannigane0@qq.com</td>\r\n        <td>Sibeal</td>\r\n        <td>Hannigane</td>\r\n      </tr>\r\n      <tr>\r\n        <td>003500339Kgli7N5t9</td>\r\n        <td>lbentham1@shutterfly.com</td>\r\n        <td>Lockwood</td>\r\n        <td>Bentham3</td>\r\n      </tr>\r\n      <tr>\r\n        <td>003500oS7XRqSuGyQ2</td>\r\n        <td>lodoran2@51.la</td>\r\n        <td>Loren</td>\r\n        <td>O\'Doran</td>\r\n      </tr>\r\n      <tr>\r\n        <td>0035003Xgg2dDhmjRB</td>\r\n        <td>bwhitley3@shutterfly.com</td>\r\n        <td>Bidget</td>\r\n        <td>Whitley</td>\r\n      </tr>\r\n      <tr>\r\n        <td>003500Sr76b5k8V6n7</td>\r\n        <td>bfrensch4@time.com</td>\r\n        <td>Bibbye</td>\r\n        <td>Frensch</td>\r\n      </tr>\r\n    </tbody>\r\n  </table>\r\n</div>\r\n<!-- Table end -->\r\n\r\n\r\n<a href=\"https://github.com/sabuhiy/mc30/blob/main/sql30/sql30_01_NextMobile%20Users.csv\" class=\"btn btn-success rounded-pill text-white\" target=\"_blank\">üíæ Download .csv</a>\r\n\r\n\r\n<h3 class=\"fs-6 my-4\">Dataset Details: <code>Nextmobile Users</code></h3>\r\n<!-- Table start -->\r\n<div class=\"table-responsive\">\r\n<table class=\"table border-success\">\r\n  <thead>\r\n    <tr>\r\n      <th scope=\"col\">Name</th>\r\n      <th scope=\"col\">Data Type</th>\r\n      <th scope=\"col\">Length</th>\r\n      <th scope=\"col\">Primary Key</th>\r\n      <th scope=\"col\">Nullable</th>\r\n    </tr>\r\n  </thead>\r\n  <tbody class=\"table-group-divider border-success\">\r\n    <tr>\r\n      <th scope=\"row\">id</th>\r\n      <td>Text</td>\r\n      <td>50</td>\r\n      <td>Yes</td>\r\n      <td></td>	  \r\n    </tr>\r\n    <tr>\r\n      <th scope=\"row\">email</th>\r\n      <td>EmailAddress</td>\r\n      <td>254</td>\r\n      <td></td>\r\n      <td>TRUE</td>	  \r\n    </tr>	\r\n    <tr>\r\n      <th scope=\"row\">first_name</th>\r\n      <td>Text</td>\r\n      <td>50</td>\r\n      <td></td>\r\n      <td>TRUE</td>	  \r\n    </tr>\r\n    <tr>\r\n      <th scope=\"row\">last_name</th>\r\n      <td>Text</td>\r\n      <td>50</td>\r\n      <td></td>\r\n      <td>TRUE</td>	  \r\n    </tr>	\r\n  </tbody>\r\n</table>\r\n</div>\r\n<!-- Table end -->'),
