/th>\r\n    </tr>\r\n  </thead>\r\n  <tbody class=\"table-group-divider border-success\">\r\n    <tr>\r\n      <th scope=\"row\">id</th>\r\n      <td>Text</td>\r\n      <td>50</td>\r\n      <td>Yes</td>\r\n      <td></td>	  \r\n    </tr>\r\n    <tr>\r\n      <th scope=\"row\">email</th>\r\n      <td>EmailAddress</td>\r\n      <td>254</td>\r\n      <td></td>\r\n      <td>TRUE</td>	  \r\n    </tr>	\r\n    <tr>\r\n      <th scope=\"row\">first_name</th>\r\n      <td>Text</td>\r\n      <td>50</td>\r\n      <td></td>\r\n      <td>TRUE</td>	  \r\n    </tr>\r\n    <tr>\r\n      <th scope=\"row\">last_name</th>\r\n      <td>Text</td>\r\n      <td>50</td>\r\n      <td></td>\r\n      <td>TRUE</td>	  \r\n    </tr>	\r\n  </tbody>\r\n</table>\r\n</div>\r\n<!-- Table end -->'),
