(2099, 708, '_resources', '<p>You can use the NextMobile_Users dataset. Ensure that you have uploaded the \"launch_date\" data, as the renewal date will be determined based on this value.</p>\r\n\r\n<h3 class=\"fs-5 my-4 text-success\">Dataset</h3>\r\n\r\n<!-- Table start -->\r\n<div class=\"table-responsive\">\r\n  <table class=\"table table-striped\">\r\n    <thead>\r\n      <tr>\r\n        <th scope=\"col\">id</th>\r\n        <th scope=\"col\">launch_date</th>\r\n      </tr>\r\n    </thead>\r\n    <tbody>\r\n      <tr>\r\n        <td>003500M7Q93CKG2je2</td>\r\n        <td>2009-02-26</td>\r\n      </tr>\r\n      <tr>\r\n        <td>003500339Kgli7N5t9</td>\r\n        <td>2004-06-04</td>\r\n      </tr>\r\n      <tr>\r\n        <td>003500oS7XRqSuGyQ2</td>\r\n        <td>2020-08-10</td>\r\n      </tr>\r\n      <tr>\r\n        <td>0035003Xgg2dDhmjRB</td>\r\n        <td>2018-02-14</td>\r\n      </tr>\r\n      <tr>\r\n        <td>003500Sr76b5k8V6n7</td>\r\n        <td>2005-10-19</td>\r\n      </tr>\r\n    </tbody>\r\n  </table>\r\n</div>\r\n<!-- Table end -->\r\n\r\n<a href=\"https://github.com/sabuhiy/mc30/blob/main/sql30/sql30_17_NextMobile%20Users.csv\" class=\"btn btn-success rounded-pill text-white\" target=\"_blank\">ð¾ Download</a>\r\n\r\n<h3 class=\"fs-6 my-4\">Dataset Details: <code>NextMobile_Users</code></h3>\r\n<!-- Table start -->\r\n<div class=\"table-responsive\">\r\n<table class=\"table border-success\">\r\n  <thead>\r\n    <tr>\r\n      <th scope=\"col\">Name</th>\r\n      <th scope=\"col\">Data Type</th>\r\n      <th scope=\"col\">Length</th>\r\n      <th scope=\"col\">Primary Key</th>\r\n      <th scope=\"col\">Nullable</th>\r\n    </tr>\r\n  </thead>\r\n  <tbody class=\"table-group-divider border-success\">\r\n    <tr>\r\n      <th scope=\"row\">id</th>\r\n      <td>Text</td>\r\n      <td>50</td>\r\n      <td>Yes</td>\r\n      <td></td>    \r\n    </tr>\r\n    <tr>\r\n      <th scope=\"row\">launch_date</th>\r\n      <td>Date</td>\r\n      <td></td>